mapper.select:
SELECT*FROM STUDIO_ATTACHMENT 
WHERE ROOM_NO = #{roomNo}
ORDER BY FILE_LEVEL

mapper.insert:
INSERT INTO STUDIO_ATTACHMENT SELECT SEQ_FNO.NEXTVAL, A.* FROM (
SELECT #{at.fileLevel} FILE_LEVEL, #{at.fileName} FILE_NAME, #{at.filePath} FILE_PATH, #{at.fileType} FILE_TYPE, #{at.roomNo} ROOM_NO FROM DUAL
) A

mapper.update:
UPDATE STUDIO_ROOM SET
ROOM_STATUS = #{roomStatus}
WHERE ROOM_NO = #{roomNo}

UPDATE STUDIO_ATTACHMENT SET
FILE_NAME = #{fileName},
FILE_PATH = #{filePath},
FILE_TYPE = #{fileType}
WHERE FILE_LEVEL = #{fileLevel}
AND ROOM_NO = #{roomNo}

UPDATE RESERVATION SET
RV_STATUS = #{rvStatus}
WHERE RV_NO = #{rvNo}
