mapper.select:
SELECT CNT, (SELECT BOARD_NAME FROM BOARD_TYPE WHERE BOARD_TYPE_NO = #{boardTypeNo} AND BOARD_CT_NO = 1) BOARD_NAME FROM (SELECT COUNT(*) CNT FROM BOARD_LIST WHERE BOARD_TYPE_NO = #{boardTypeNo} AND BOARD_STATUS = 'Y'
AND BOARD_CT_NO = #{boardCTNo}
)

SELECT CNT,
	(SELECT BOARD_NAME FROM BOARD_TYPE
		WHERE BOARD_TYPE_NO = #{boardTypeNo} AND BOARD_CT_NO = 1) BOARD_NAME
FROM (SELECT COUNT(*) CNT FROM BOARD_LIST
		WHERE BOARD_TYPE_NO = #{boardTypeNo} AND BOARD_STATUS = 'Y'

	AND BOARD_CT_NO = #{boardCTNo}	



	AND 



			BOARD_TITLE LIKE #{val}


			BOARD_CONTENT LIKE #{val}


			( BOARD_TITLE LIKE #{val} OR BOARD_CONTENT LIKE #{val} )


			MEMBER_NM LIKE #{val}



)

SELECT * FROM BOARD_LIST
WHERE BOARD_STATUS = 'Y'
AND
BOARD_TYPE_NO = #{boardTypeNo}

	AND BOARD_CT_NO = #{boardCTNo}	

ORDER BY BOARD_NO DESC

SELECT * FROM BOARD_LIST
WHERE BOARD_STATUS = 'Y'
AND
BOARD_TYPE_NO = #{boardTypeNo}


	AND BOARD_CT_NO = #{boardCTNo}	


	AND 



			BOARD_TITLE LIKE #{val}


			BOARD_CONTENT LIKE #{val}


			( BOARD_TITLE LIKE #{val} OR BOARD_CONTENT LIKE #{val} )


			MEMBER_NICK LIKE #{val}



ORDER BY BOARD_NO DESC

SELECT * FROM BOARD_ATTACHMENT
WHERE BOARD_NO = #{boardNo}
ORDER BY BOARD_AT_LV

SELECT ATCOUNT FROM (SELECT COUNT(BOARD_AT_NO) AS ATCOUNT, BOARD_NO FROM BOARD_ATTACHMENT
GROUP BY BOARD_NO)
WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD SET
BOARD_READ_COUNT = BOARD_READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.insert:
INSERT INTO BOARD_ATTACHMENT SELECT SEQ_BANO.NEXTVAL, A.* FROM (
SELECT #{at.atLv} BOARD_AT_LV, #{at.atPath} BOARD_AT_PATH, #{at.atName} BOARD_AT_NM, #{at.boardNo} BOARD_NO FROM DUAL
) A
