mapper.select:
SELECT COUNT(*) FROM MEMBER
WHERE MEMBER_STATUS = 'Y'
AND MEMBER_ID = #{id}

mapper.update:
UPDATE MEMBER SET
MEMBER_PHONE = #{memberPhone},
MEMBER_EMAIL = #{memberEmail},
MEMBER_NICK = #{memberNick}

	,MEMBER_IMAGE = #{memberImage}

WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_PW = #{memberPw}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_STATUS = 'N'
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_IMAGE = DEFAULT
WHERE MEMBER_NO = #{memberNo}
